 $sql_arr['is_deleted'];                $id = $sql_arr['id'];                $sql1 = $mysqli->query("select * from biz_man_info where id=$bizman");                $sql_arr1 = mysqli_fetch_assoc($sql1);                $bizmanname=$sql_arr1['biz_man_name'];                $bizmanPhone=$sql_arr1['biz_man_phone'];                $bizmanAddress=$sql_arr1['biz_man_address'];                $bizmanIsOn=$sql_arr1['is_deleted'];                $bizmanTime=$sql_arr1['gmt_modified'];                $sql2 = $mysqli->query("select * from replenishman_info where id=$replenishman");                $sql_arr2 = mysqli_fetch_assoc($sql2);;                $replenishmanName = $sql_arr2['replenishman_name'];                $replenishmanPhone = $sql_arr2['replenishman_phone'];                $replenishmanAddress = $sql_arr2['replenishman_address'];                $replenishmanIsOn = $sql_arr2['is_deleted'];                $replenishmanTime = $sql_arr2['gmt_modified'];                $online='';                $operate='';                if($isOn==0){                    $online = "<td class='td-status'><span class='label label-success radius'>已上线</span></td>";                    $operate="<td class='f-14 td-manage'><a style='text-decoration:none' onClick='online(this.parentNode,1)' href='javascript:;' title='下线'><i class='Hui-iconfont' style='font-size: large'>&#xe6de;</i></a>                                <a style='text-decoration:none' onClick='edit(this.parentNode)' href='javascript:;' title='编辑'><i class='Hui-iconfont' style='font-size: large'>&#xe6df;</i></a></td>";                }else{                    $online = "<td class='td-status'><span class='label label-danger radius'>已下线</span></td>";                    $operate="<td class='f-14 td-manage'><a style='text-decoration:none' onClick='online(this.parentNode,0)' href='javascript:;' title='上线'><i class='Hui-iconfont' style='font-size: large'>&#xe6dc;</i></a>                        <a style='text-decoration:none' onClick='edit(this.parentNode)' href='javascript:;' title='编辑'><i class='Hui-iconfont' style='font-size: large'>&#xe6df;</i></a></td>";                }                echo "<tr class='text-c'>                        <td><input type='checkbox' value='$id' name=''></td>                        <td class='menu_id' hidden='hidden'>$id</td>                        <td class='is_on' hidden='hidden'>$isOn</td>                        <td class='editable-cname'>$name</td>                        <td>$uuid</td>                        <td class='bizman_name' hidden='hidden'>$bizmanname</td>                        <td class='bizman_phone' hidden='hidden'>$bizmanPhone</td>                        <td class='bizman_address' hidden='hidden'>$bizmanAddress</td>                        <td class='bizman_is_on' hidden='hidden'>$bizmanIsOn</td>                        <td class='bizman_time' hidden='hidden'>$bizmanTime</td>                                                <td class='replenish_name' hidden='hidden'>$replenishmanName</td>                        <td class='replenish_phone' hidden='hidden'>$replenishmanPhone</td>                        <td class='replenish_address' hidden='hidden'>$replenishmanAddress</td>                        <td class='replenish_is_on' hidden='hidden'>$replenishmanIsOn</td>                        <td class='replenish_time' hidden='hidden'>$replenishmanTime</td>                        <td>$address</td>                        <td class='text-l'><a class=\"maincolor\" href=\"javascript:;\" onClick=\"showReplenishManInfo(this.parentNode)\">$replenishmanName</a></td>                        <td class='text-l'><a class=\"maincolor\" href=\"javascript:;\" onClick=\"showBizManInfo(this.parentNode)\">$bizmanname</a></td>                        <td>$time</td>",$online,$operate,"</tr>";            }            ?>            </tbody>        </table>    </div></div><!--_footer 作为公共模版分离出去--><script type="text/javascript" src="../lib/jquery/1.9.1/jquery.min.js"></script><script type="text/javascript" src="../lib/layer/2.4/layer.js"></script><script type="text/javascript" src="../static/h-ui/js/H-ui.min.js"></script><script type="text/javascript" src="../static/h-ui.admin/js/H-ui.admin.js"></script> <!--/_footer 作为公共模版分离出去--><!--请在下方写此页面业务相关的脚本--><script type="text/javascript" src="../lib/My97DatePicker/4.8/WdatePicker.js"></script><script type="text/javascript" src="../lib/datatables/1.10.0/jquery.dataTables.min.js"></script><script type="text/javascript" src="../lib/datatables/jquery.jeditable.js"></script><script type="text/javascript" src="../lib/laypage/1.2/laypage.js"></script><script type="text/javascript">    $(window).load(function () {        $("#refresh").click();    });    var oTable = $('.table-sort').dataTable({    });    $('.editable-cname', oTable.fnGetNodes()).editable('../base/editRackName.php', {        "callback": function (sValue, y) {            var aPos = oTable.fnGetPosition( this );            oTable.fnUpdate( sValue, aPos[0], aPos[1]);        },        "submitdata": function (value, settings) {            var name = $(this).val();            var id = $(this).siblings('.menu_id').html();            return {                "name":name,                "id": id            };//这里你编辑的内容默认以“value”发送到后台        },        "height": "20px",        "width": "120px"    });    $(".table-sort tbody tr").dblclick(function (){        var isOn =$(this).children('.is_on').html();        if(isOn == 1){            layer.msg('已下线不能编辑！', {                icon: 2,                time: 1500            });            return false;        }        var id =$(this).children('.menu_id').html()        var url ='rack-edit.php?id='+id;        var index = layer.open({            type: 2,            title: '货架编辑',            content: url        });        layer.full(index);    });    function showReplenishManInfo(ele) {        var name = $(ele).siblings('.replenish_name').html();        var phone = $(ele).siblings('.replenish_phone').html();        var address = $(ele).siblings('.replenish_address').html();        var isOn = $(ele).siblings('.replenish_is_on').html()==0?'是':'否';        var time = $(ele).siblings('.replenish_time').html();        layer.open({            type: 1,            area: ['500px','300px'],            fix: false, //不固定            maxmin: true,            shade:0.4,            title: '查看补货员信息',            content: "<table class='table table-border table-bordered table-bg mt-20'><thead><tr><th colspan='2' scope='col'>补货员信息</th></tr></thead><tbody>" +            "<tr><th width='30%'>姓名</th><td><span id='lbServerName'>"+name+"</span></td>" +            "</tr><tr><td>手机号</td><td>"+phone+"</td></tr>" +            "<tr><td>住址</td><td>"+address+"</td></tr>" +            "<tr><td>是否在职</td><td>"+isOn+"</td></tr>" +            "<tr><td>更新时间</td><td>"+time+"</td></tr></tbody></table>"        });    }    function showBizManInfo(ele) {        var name = $(ele).siblings('.bizman_name').html();        var phone = $(ele).siblings('.bizman_phone').html();        var address = $(ele).siblings('.bizman_address').html();        var isOn = $(ele).siblings('.bizman_is_on').html()==0?'是':'否';        var time = $(ele).siblings('.bizman_time').html();        layer.open({            type: 1,            area: ['500px','300px'],            fix: false, //不固定            maxmin: true,            shade:0.4,            title: '查看业务员信息',            content: "<table class='table table-border table-bordered table-bg mt-20'><thead><tr><th colspan='2' scope='col'>业务员信息</th></tr></thead><tbody>" +            "<tr><th width='30%'>姓名</th><td><span id='lbServerName'>"+name+"</span></td>" +            "</tr><tr><td>手机号</td><td>"+phone+"</td></tr>" +            "<tr><td>住址</td><td>"+address+"</td></tr>" +            "<tr><td>是否在职</td><td>"+isOn+"</td></tr>" +            "<tr><td>更新时间</td><td>"+time+"</td></tr></tbody></table>"        });    }    function datadel(type) {        var vals = [];        $('input:checkbox:checked').each(function (index, item) {            var value = $(this).val();            if (value > 0) {                vals.push($(this).val());            }        });        var text = '';        if(0==type){            text = '确定要上线这';        } else {            text = '确定要下线这';        }        if (vals.length < 1) {            layer.msg('请选择至少一行数据', {                icon: 2,                time: 1500            });        } else {            var ids = vals.join(',');            commonOnline(text + vals.length + '个货架吗？',ids,type);        }    }    function edit(ele) {        var isOn =$(ele).siblings('.is_on').html();        if(isOn == 1){            layer.msg('已下线不能编辑！', {                icon: 2,                time: 1500            });            return false;        }        var id = $(ele).siblings('.menu_id').html();        var url ='rack-edit.php?id='+id;        var index = layer.open({            type: 2,            title: '新闻编辑',            content: url        });        layer.full(index);    }    function online(ele,type) {        var id = $(ele).siblings('.menu_id').html();        var msg='';        if(0==type){            msg = '确定要上线吗？';        } else {            msg = '确定要下线吗？'        }        commonOnline(msg,id,type);    }    function commonOnline(msg,id,type){        layer.confirm(msg,function(index) {            $.ajax({                type: "POST",                url: "../base/rackOnline.php",                dataType: "json",                data: {"id": id, "type": type},                success: function (json) {                    if (json.success == 1) {                        layer.msg(json.msg, {                            icon: 1,                            time: 1500                        },function () {                            window.location.href = $("#refresh").attr('href');                        });                    } else {                        layer.msg(json.msg, {                            icon: 2,                            time: 1500                        },function () {                            window.location.href = $("#refresh").attr('href');                        });                    }                }            });        });    }    function add_rack() {        var index = layer.open({            type: 2,            title: '添加货架',            content: 'rack-edit.php?id=0'        });        layer.full(index);    }</script></body></html>